class ChildrenController < ApplicationController

  before_filter :find_person, :except => [:index, :query]

  def index

    @icoFolder = "data:image/png;base64,"

    @section = "Home"

    @targeturl = "/logout"

    @targettext = "Logout"

    @admin = ((User.current_user.role.strip.downcase.match(/admin/) rescue false) ? true : false)

    render :layout => "facility"

  end

  def view

    @section = "View Local Records"

    @targeturl = "/"

    render :layout => "facility"

  end

  def search

    @section = "Search for Record"

    @targeturl = "/"

    render :layout => "facility"

  end

  def destroy
    child = Child.find(params[:id])
    child.update_attributes(:voided => true, :voided_by => current_user.id, :voided_date => Time.now)
    redirect_to "/" and return
  end

  def create

    params[:child].delete("_rev")

    Child.create(params[:child])

    redirect_to "/" and return

  end

  def update
    params[:child].merge!("updated_by" => current_user.id)
    @child.update_attributes(params[:child])

    redirect_to "/" and return

  end

  def edit

    @section = "Edit Child"

    @targeturl = "/"

    @nationality = Nationality.all.collect(&:id)
    
    @districts = District.all.collect(&:name)

    render :layout => "facility"

  end

  def new

    @section = "New Child"

    @targeturl = "/"

    render :layout => "facility"

  end

