  def search_by_fullname

    name = (params[:id].strip.split("+") rescue [])

    name = (params[:id].strip.split(" ") rescue []) if name.empty? or name.length < 2

    results = []

    if name.length > 1

      children = Child.by_name.keys([[name[0].soundex, name[1].soundex]]).limit(10).each

    else

      children = Child.by_name.limit(10).each

    end

    children.each do |child|

      next if child.record_status.to_s.downcase == "voided" or child.voided == true

